import React, {useEffect,useState} from 'react';
import {socket} from '../utils/socket';

export default function Participant(){
  const [question,setQuestion]=useState(null);
  const [time,setTime]=useState(0);
  const [votes,setVotes]=useState(0);
  const [connections,setConnections]=useState(0);
  const [stamp,setStamp]=useState('');
  const [voteLeft,setVoteLeft]=useState(3);

  useEffect(()=>{
    socket.on('init',(data)=>{
      setQuestion(data.question);
      setTime(data.time);
      setVotes(data.votes);
    });
    socket.on('questionUpdate',(q)=>{
      setQuestion(q);
      setVoteLeft(3); // Ë≥™ÂïèÂ§â„Çè„Å£„Åü„ÇâÊäïÁ•®Êï∞„É™„Çª„ÉÉ„ÉàÊÉ≥ÂÆö(„Çµ„Éº„Éê„ÅßÁÆ°ÁêÜ„Å†„ÅåUIÂÅ¥„ÇÇÂêà„Çè„Åõ„Çã)
    });
    socket.on('timeUpdate',(t)=>setTime(t));
    socket.on('voteUpdate',({votes})=>{setVotes(votes);});
    socket.on('connectionsUpdate',(c)=>setConnections(c));

    return ()=>{
      socket.off('init');
      socket.off('questionUpdate');
      socket.off('timeUpdate');
      socket.off('voteUpdate');
      socket.off('connectionsUpdate');
    };
  },[]);

  function sendStamp(type){
    socket.emit('sendStamp',{type});
    setStamp(type);
    setTimeout(()=>setStamp(''),1500);
  }

  function sendVote(){
    if(voteLeft>0){
      socket.emit('sendVote');
      setVoteLeft(voteLeft-1);
    }
  }

  return (
    <div className="p-4 text-center">
      <h1 className="text-2xl font-bold mb-4 text-accent">ÂèÇÂä†ËÄÖÁîªÈù¢</h1>
      <div className="mb-4">
        <div className="text-xl mb-2">{question?question.text:"Ë≥™ÂïèÂæÖ„Å°..."}</div>
        <div className="text-lg">ÊÆã„ÇäÊôÇÈñì: {time}s</div>
        <div className="text-sm text-gray-600">ÂèÇÂä†ËÄÖÊï∞: {connections}</div>
      </div>
      <div className="mb-4">
        <button onClick={()=>sendStamp('like')} className="mx-2 text-2xl">üëç</button>
        <button onClick={()=>sendStamp('wow')} className="mx-2 text-2xl">üò≤</button>
        <button onClick={()=>sendStamp('agree')} className="mx-2 text-2xl">üó≥Ô∏è</button>
        <button onClick={()=>sendStamp('question')} className="mx-2 text-2xl">‚ùì</button>
      </div>
      <div className="mb-4">
        <button onClick={sendVote} className="bg-accent text-white px-4 py-2 rounded">„ÇÇ„Å£„Å®ËÅû„Åç„Åü„ÅÑÔºÅ</button>
        <div className="text-sm mt-2">ÊÆã„ÇäÊäïÁ•®: {voteLeft}Á•®</div>
      </div>
      {stamp && (
        <div className="fixed top-1/2 left-1/2 text-5xl animate-bounce">
          {stamp==='like'?'üëç': stamp==='wow'?'üò≤': stamp==='agree'?'üó≥Ô∏è':'‚ùì'}
        </div>
      )}
    </div>
  );
}
