async function handleLogin(e) {
    e.preventDefault();
    try {
      const res = await fetch(`${process.env.REACT_APP_BACKEND_URL}/admin/login`, { // 環境変数を使用
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password })
      });
      const data = await res.json();
      if (data.success) {
        onLogin(data.token);
      } else {
        setError('ログイン失敗');
      }
    } catch (err) {
      setError('サーバーエラー');
    }
  }
